from anthropic import Anthropic

client = Anthropic()

# 定义工具
tools = [
    {
        "name": "search_web",
        "description": "搜索互联网获取最新信息",
        "input_schema": {
            "type": "object",
            "properties": {
                "query": {"type": "string"}
            },
            "required": ["query"]
        }
    }
]

# Agent 循环
response = client.messages.create(
    model="claude-sonnet-4-5-20250514",
    max_tokens=4096,
    tools=tools,
    messages=[{"role": "user", "content": "分析最近的 AI 论文趋势"}]
)
